"use strict";const scrollUp=document.querySelector(".scrollup");window.addEventListener("scroll",l=>{l.preventDefault(),window.pageYOffset>100?(scrollUp.classList.add("scrollup--show"),scrollUp.classList.remove("scrollup--hide")):(scrollUp.classList.add("scrollup--hide"),scrollUp.classList.remove("scrollup--show"))}),scrollUp.addEventListener("click",l=>{l.preventDefault(),window.scrollTo({top:0,behavior:"smooth"})});