"use strict";const scrollUp=document.querySelector(".scroll-up");window.addEventListener("scroll",o=>{o.preventDefault(),window.pageYOffset>100?(scrollUp.classList.add("opacity-on"),scrollUp.classList.remove("opacity-off")):(scrollUp.classList.add("opacity-off"),scrollUp.classList.remove("opacity-on"))}),scrollUp.addEventListener("click",o=>{o.preventDefault(),window.scrollTo({top:0,behavior:"smooth"})});